import React, { useState } from "react";

const Writecontent = () => {
  let [note,setNote] = useState({
    title:"",
    content:""
  });

  onsubmit=(event)=>{
    let {value,name} = event.target;
    setNote()
  }
  return (
    <>
      <div className="min-h-[30%] bg-white mt-6 mb-8 w-[35%] relative rounded-md shadow-md shadow-slate-400 mx-auto">
        <input
        name="title"
          className="text-xl font-semibold absolute top-3 left-3 outline-0 w-[90%]"
          placeholder="Add Input Title"
          value={note}
        ></input>
        <textarea
        name = "content"
          placeholder="write a note..."
          className="text-md absolute top-14 w-[90%] h-[50%] outline-0 ml-4"
          value={note}
        ></textarea>
        <button className="absolute bottom-0 right-1 top-52 h-12 w-12 rounded-full text-4xl flex justify-center items-center pb-2 pr-1 text-yellow-300 font-semibold shadow-lg shadow-slate-500 bg-white">
          +
        </button>
      </div>
    </>
  );
};

export default Writecontent;
